import React, {useState} from "react";
import Modal from "react-modal";
import pub from '../images/pub.jpeg';

const Pub = () => {
  const [modal_IsOpen, set_IsOpen] = useState(false);

  const openModal = () => {
    set_IsOpen(true);
  }

  const closeModal = () => {
    set_IsOpen(false);
  }

  return (
    <>
      <img onClick={openModal} className="square" src={pub} />
      <Modal 
        isOpen={modal_IsOpen}
        onRequestClose={closeModal}
        className="modal"
        ariaHideApp={false}
        closeTimeoutMS={200}
        onAfterOpen
        >
        <div className="container-img">
          <img className="img" src={pub} />
          <button onClick={closeModal} className="btn">Back</button>
        </div>
        </Modal>
    </>
  )
}

export {Pub as default};
